"use strict";(self.webpackChunkujon_github_io=self.webpackChunkujon_github_io||[]).push([[299],{634:n=>{n.exports=JSON.parse('{"permalink":"/ko/blog/placeholder","source":"@site/i18n/ko/docusaurus-plugin-content-blog/2025-09-08/index.md","title":"\ud50c\ub808\uc774\uc2a4\ud640\ub354","description":"\uc544\ub219\uc544 \uc154\ud558\ub878\uc624 \uc14d\ucc98\uc218\ub97c \uc5bc\ub801\ube48\ubc14\uc640, \ud6c4\uc8f0\ub098 \uad7f\ud504\ub2e4 \ub4dc\uc988\uc790\uba38\uc2a8\ub2e4\ub294 \ub2e8\ub974\uac00 \ud558\ub2e4\uc2dc\ub2e4. \uc790\ub4f1\uc5ec\uc5b4\uc11c \uc1a8\ub219\uc18c\ud5c8 \uc0bc\uac15\uc190\ub9c8\uc73c\uc7ac \ub97b\uc18c\uacac\ub8e8\ucc28 \ud558\ub2e4\uc7a1\uc744 \uc544\uc544\ud558\uc778\uc744 \ub974\uc5d8 \ube0c\ud168, \uc988\uc774\uac00 \uc57d\uac15\ubcf4\ub974\uc544\ub2e4. \uc785\ub85c \ub7a0\uc774\ub108\uc758 \uae68\ub370\uc6c5\ub294 \uc5e0\uc571\ub358\ub62c\uba70","date":"2025-09-08T20:45:00.000Z","tags":[],"readingTime":0.9,"hasTruncateMarker":false,"authors":[{"name":"\uc720\uc885\ud638","title":"Ex-42dot Engineer","url":"https://linkedin.com/in/ujon","page":null,"socials":{"linkedin":"https://www.linkedin.com/in/ujon/","github":"https://github.com/ujon"},"imageURL":"https://github.com/ujon.png","key":"ujon"}],"frontMatter":{"slug":"placeholder","title":"\ud50c\ub808\uc774\uc2a4\ud640\ub354","authors":"ujon","date":"2025-09-08 20:45:00 +9000","tags":[]},"unlisted":false}')},7636:(n,t,e)=>{e.r(t),e.d(t,{assets:()=>a,contentTitle:()=>i,default:()=>p,frontMatter:()=>u,metadata:()=>o,toc:()=>c});var o=e(634),s=e(4848),r=e(8453);const u={slug:"placeholder",title:"\ud50c\ub808\uc774\uc2a4\ud640\ub354",authors:"ujon",date:"2025-09-08 20:45:00 +9000",tags:[]},i="Placeholder",a={authorsImageUrls:[void 0]},c=[];function l(n){const t={p:"p",...(0,r.R)(),...n.components};return(0,s.jsx)(t.p,{children:"\uc544\ub219\uc544 \uc154\ud558\ub878\uc624 \uc14d\ucc98\uc218\ub97c \uc5bc\ub801\ube48\ubc14\uc640, \ud6c4\uc8f0\ub098 \uad7f\ud504\ub2e4 \ub4dc\uc988\uc790\uba38\uc2a8\ub2e4\ub294 \ub2e8\ub974\uac00 \ud558\ub2e4\uc2dc\ub2e4. \uc790\ub4f1\uc5ec\uc5b4\uc11c \uc1a8\ub219\uc18c\ud5c8 \uc0bc\uac15\uc190\ub9c8\uc73c\uc7ac \ub97b\uc18c\uacac\ub8e8\ucc28 \ud558\ub2e4\uc7a1\uc744 \uc544\uc544\ud558\uc778\uc744 \ub974\uc5d8 \ube0c\ud168, \uc988\uc774\uac00 \uc57d\uac15\ubcf4\ub974\uc544\ub2e4. \uc785\ub85c \ub7a0\uc774\ub108\uc758 \uae68\ub370\uc6c5\ub294 \uc5e0\uc571\ub358\ub62c\uba70\n\uae61\ub917\uac00\uc544\ub2e4. \ubd80\uc220\ud2dc \ubd9a\uae1c\ubc8c\ub300\uace0 \uc11c\uc6b8\ud638\uac78\uc758 \ube44\ubaa8\uc5c9 \uc9c4\ubc18\ub2e4. \ub9c1\ubc14\ud5c8\ub294 \uc544\ud5e4\ub9c8\uac00\uac00 \uc9c4\uc81d\uc73c\uc138\uc694 \ub098\uba40\uc0ac\uace0 \ube4c\uc98c\uace1\ub2f4\ub294 \ud558\ubc30\ub2e4\uc694\ub85c\uc11c \ub4dc\uc831\uc881\ud568\uc758. \ub9c9\ub3c4\uc6e8\ub2e8\uace0\ub3c4 \ub150\uc5b4\ub098\ub70c\ub77c, \uc234\uc36c\uc740 \ud79d\uc5c4\ud5e8\uac14\uc744 \uc2a4\ub0a9\ub294\ub2e4 \uc544\uae34\ub294\ub2e4 \ub4dc\uba58 \uac70\uac00\ub878\ub048\uc744\n\ud0b7\ubb61\ud788\uc6b4\ucd08\ud788\uace0, \uac00\ube28\ud4e8\uc5d0. \ub5cd\ud14c\uad6c\ub098 \uc120\ub9c8\uc544\ub294 \uc624 \ub51c\uac04\uc774\ub098 \uc0ac\uc774\uae30\uc5b4 \ub9ce\uc0c8\ud5c8\uba70 \uc0ac\ud0d3\ud2b8\uac00 \ud328\uc624\ub97c \ubbf9\ub7f0\uac04\uc200\uc6b0\ub2e4\uac00 \uc808\ub194 \uc73c\ub3c8\ub3a1\uc774.\n\uc5ec\uc804\uc740 \uac9c\uc8e8\uc73c\ub85c \uacf4\uc988\uac00, \ud600\uc624 \ub3e0\uc988\uc5f0\ub2e4. \uc0bd\ub518\uc0ac\uc65c\ub0ab\uce28\uace0 \uc704\uc5cd\uc73c\ub85c \uc5d0\uc6b0\ub450 \uc54c\uc2a4\ub97c \uc774\uae30\ud380\ub2e4 \ub204\ucc3d\uc740 \ub381\ub978\uc6b1\uc624\ub97c \ucd0c\uafc8\ud305\uc758, \uac15\ub4dc\ub0d0. \uc57c\uc98c\ucd08\ub97c \ud478\ub124\ub2e4 \uad88\ud62c\ub77c\ub0c9\uc559\uc28c\uc744 \ub465\ub38c\ud2b8\ub97c \ud0ef\ud6be\ubab0\ucd08\ubc1c\uc744 \uc7ac\ud3a0\uc73c\uac74 \uc2a4\uac00\ubaa8\uae30\ub294 \ubc14\uacf5\uc778\ucd1d\uc744\n\ubbf8\ubcc0\uc73c\ub85c. \uc548\ub79c\uc9c0\ub07c\uac01\uc774\uc5b4 \uc988\ub9ac\ub7a8 \ub2c8\ub77c\ub300\uac8c\uac00 \uac04\ub354\uc57c\ub9d0\ub85c \uc2b9\uc139\uc784\uc758 \ubb1c\ubc84\ub77c\uc5d0\ub294 \uc560\uc84d\ud2bc \uc0c8\uc250 \uac15\ud2f0\ud604\uc854\ucc2c\uc744\uae4c. \ud2f0\ubbf8\ubabd\uc2dc\ub529\ub2e4 \ud390\uc624\ub7ec\ub4c4 \uc853\ud29c\uc790\ub86d\uc744, \uc6b0\uc5b5\uacfc \uaca8\uc800\uc5b4\ud664\ub2e4 \uc5b4\uc694\ucc2c\uaddc\ubc84 \ub9db\ud5d0\uc9c0\uc5b4 \uc544\uc5b4\uc2b9\ud559\ud5e4\uc758 \uc120\uac01\ub3e0\uc784\ub294\ub2e4. \uc5d4\uc564\ub9d0\uad70\uc5d0\uc11c\n\uc789\ucf11\ube0d\ub098\uc0b0\ub2e4 \ud30c\uc1a8\uac00 \uba70\ub7ec\ub97c \uace0\uc560\ub514\ubf55\uc774\ub294 \ube59\ub9e4\uae46\uc190\uc758 \ub178\uc784\uc62c\ubc34\uc774\uc5b4 \ub77c\uc60c\uc774\ub124\ub3c4 \ub2e4\ub3c4\ud0c8\uc73c\uae31. \uad50\ub514\ub97c \uc0b0\uc774\ub098\ub2e4\uac00 \uae30\ub514\ubdf0\ub294 \uc774\uc1fc\uc790\uc790\uc774\ub354\ub124\ub97c \ub610\uac01\ub9ac\ub205\ub8e8\uc6b1\uc774\uace0 \ub2c8\ud130\ub2c8\ub9c8\uc544, \uc2dc\uc54c\uc14b\uad34\uc5b4 \ud5e8\uc640\ucc48\ub4e4. \ub448\ubbc4\uacfb\ud2f4\uc740 \uac70\uc988\ub294 \ud6c4\uc774\uc639\uc758\n\uc5b4\uc918\uc545\uc9c0\uc988\ub97c \uacac\ub9c8\ud0a4\uc628\uace0, \uc73c\uc560\ub369\ub2e4 \ub294\uc5bd\uac1c\ub294. \uce31\uba1c\uc571\uacfc \uc2f8\uc751\ud638\uac8c \ub144\uc288\uc11c\uc2e0\uc774\uc5b4 \uae48\uc5d4\ub808 \uc870\uc774\ud590\ub374\uc11c\uc5d0.\n\ube4c\ud3ec\uc638\ube48\uc774 \ud0a4\ub208\uc0ac\uac4b\uc5d0 \ub0b4\ub514 \uae30\ucfe4\ub121\ube0d\ubc14\uae30\uc0ac\ub974 \ub990\uc6e1\uac74, \ub055\ub3c4\uc6b4\uac8c \uc6b0\uc2a4\uac00. \ud790\ud0dc\uc680\uc774 \ubc84\ucdb4\uc789\uc988 \ub354\uc560\ubb28\uc798\uc774 \ubc14\ud558\ub2c8 \uc1ac\ub4f1\uc790\ub2e4. \uac01\ub2e8\uc849\ub798\ub2e4 \ubcf4\uc5c9\uc68d\ube44 \ubb1c\uc54c\ubd80\ub39f\uc5b4\uc11c \ub304\uc6b4\ub4ed\uc5d0\uc628 \uc77c\ub4f1\ub864\ud0e4\uc758 \ub974\ud1a0\ub370\ub108\uc640 \uc0b0\uc548\uc74d\uc2dc\ub2e4. \uc11c\ub9bc\ub4dc\ud598\ub988\uada4\uc5b8 \uc138\uc544\ub2e4\n\uc774\ub2c8\uac04\ucc45\uc774\ub2e4 \uc2ff\uc560\uc5d0 \ub3d6\uc5b4\ub85c \uc560\ubcf4\ub294 \uc796\uc8f0\ub098\uc758. \uace0\ub179\uae4c\ub2f5 \ud558\uc19d\ub2c8\ub2e4 \uc9c0\uac00\uc5d0 \ucd08\uc544, \ub09c\ub0bc\uc774\uc5b4, \uac00\ube10\uc5b4\uc11c \ubfe0\ub7ec\ub140\uae30\ud56d \uc854\uace0\uc5ec\ub9cc\ub294 \uc559\uc5ec\ubd04\uc5d0 \ud168\uc774\uc314\ud788\ub2e4. \uc73c\uc5b4 \uac1c\uc751\uc740\uac00 \uac70\uc18c\uc790\uc0ac\ud734\ub987\ub294\ub2e4 \ucd95\ub9ac\uc18c\uc155\uae30, \uae34\ub784\ud788\uc758 \uc778\ub7ec\ubbf9\ub7ec\ub38c\ub77c\uc5b4\uc758\n\uce88\uce20\ub7f0\uc0cc\uc758, \uc790\ud574\ub3dd\uc138\uc778\uc5d0\uc11c. \uac74\ud574\ub188 \ubb34\ucd08\ub97c \uc6cd\ub2c8\uc544\ub2e4 \ubd84\ud78f\uc5d8\uae30\uc5d0 \ud5da\uc2dc\uc120\ub798\ub9cc \uc6b1\uc988\uc54c\uace8\uc2dc\uae44\uc774 \uc5d0\uc9e0\uc73c\uc880\uc744 \ub39d\ub194\uc544\uc544\ub294."})}function p(n={}){const{wrapper:t}={...(0,r.R)(),...n.components};return t?(0,s.jsx)(t,{...n,children:(0,s.jsx)(l,{...n})}):l(n)}},8453:(n,t,e)=>{e.d(t,{R:()=>u,x:()=>i});var o=e(6540);const s={},r=o.createContext(s);function u(n){const t=o.useContext(r);return o.useMemo(function(){return"function"==typeof n?n(t):{...t,...n}},[t,n])}function i(n){let t;return t=n.disableParentContext?"function"==typeof n.components?n.components(s):n.components||s:u(n.components),o.createElement(r.Provider,{value:t},n.children)}}}]);